sys_variable_value.value:
(function execute(inputs, outputs) {

var tweet = inputs.incident.getValue('short_description');

var nonce = Math.random().toString(36).substring(2, 15) +
    Math.random().toString(36).substring(2, 15);

    var timeStamp = Math.random(new GlideDateTime().getNumericValue() / 1000);

    var autSign = 
        'oauth_consumer_key=' + percentEncode(inputs.consumer_key) + '&' + 
        'oauth_nonce=' + percentEncode(nonce) + '&' + 
        'oauth_signature_method=HMAC-SHA1&' +
        'oauth_timestamp=' + timeStamp + '&' +
        'oauth_token='  + percentEncode(inputs.access_token) + '&' +
        'oauth_version=1.0&' +
        'status=' + percentEncode(tweet);

        var baseString = 
            "post&" + 
            percentEncode(inputs.endpoint) + '&' + percentEncode(autSign);

        var signingKey = percentEncode(inputs.consumer_secret) +
            "&" + percentEncode(inputs.access_token_secret);

        var encrypted = x_220561_twittertw.CryptoJS.HmacSHA1(baseString, signingKey);
        var signature =x_220561_twittertw.CryptoJS.enc.Base64.stringify(encrypted)

        var qs = '?auth_consumer_key=' +inputs.consumer_key + '&' +
            'oauth_once=' + nonce + '&' +
            'oauth_signature=' + percentEncode(signature) + '&' +
            'oauth_signature_method=HMAC-SHA1&' +
            'oauth_timestamp=' + timeStamp + '&' +
            'oauth_token=' + inputs.access_token + '&' +
            'oauth_version=1.0&' + 
            'status=' + percentEncode(tweet);

        outputs.endpointAndQuery = (inputs.endpoint + qs).toString();

    function percentEncode(inp) {
        var char, charCode, i;
        var encodedString = '';

        for (i = 0; i < inp.length; i++) {
            char = inp.charAt(i);
            if ((char >= '0' && char <= '9') ||
                (char >= 'A' && char <= 'Z') ||
                (char >= 'a' && char <= 'z') ||
                (char == '-') || (char == '.') ||
                (char == '_') || (char == '~')) {
                encodedString += char;
                }else {
                    charCode = inp.charCodeAt(i);
                    encodedString += '%' + charCode.toString(16).toUpperCase();
                }
        }
        return encodedString;
    };
})(inputs, outputs);

